import React, { Component} from "react";
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state= {
      animes: [],
      isLoaded: false,
    }
  }

  componentDidMount() {

    fetch('http://localhost:8080/Anime')
        .then(res => res.json())
        .then(json => {
          this.setState({
              isLoaded: true,
              animes: json,
          })
        });
  }

render() {

    var {isLoaded, animes} = this.state;


          return(
              <div className="App">

                  <ul>
                      {animes.map(anime => (
                          <li key={anime.animeID}>
                              <h1> Titel: {anime.titel}</h1>
                              <h2> Folgen: {anime.folgen}</h2>
                              <h3> Wertung: {anime.wertung}</h3>
                              <h4> Genre: {anime.genre}</h4>
                              <img src={anime.img} alt=''/>
                          </li>
                          ))};
                      </ul>


                  </div>

          );










}







}
export default App
