import {useEffect, useState} from "react";

export const AddToFavorite = (props) => {
    const [buttonPressed, setButtonPressed] = useState(false);
    const [whichId, setWhichId] = useState('')
    const newFavorite = !props.favorite;
    useEffect( () => {
        const requestOptions = {
            method: 'PUT',
            headers: {
            'Content-Type': 'application/json'
            },
            body: JSON.stringify({titel: props.titel,
                                        folgen: props.folgen,
                                        wertung: props.wertung,
                                        animeID: props.animeID,
                                        img: props.img,
                                        favorite: newFavorite})
        };
        fetch(`http://localhost:8080/Anime/${whichId}`, requestOptions)
            .then(response => response.json())
    }, [buttonPressed])


    function clickHandler(animeID){
        setWhichId(animeID)
        setButtonPressed(!buttonPressed)
    }




    return(
    <button className="addFavoriteButton" onCLick={clickHandler(animeID)}>*</button>
    )
}
