import axios from "axios";
import App from "../App";
import {useEffect, useState} from "react";

export const AddToFavorite = (props) => {
    const animeID = props
    const [buttonPressed, setButtonPressed] = useState(false);
    const [whichId, setWhichId] = useState('')

    useEffect( () => {
        const requestOptions = {
            method: 'PUT',
            headers: {
            'Content-Type': 'application/json'
            },
            body: ''
        };
        fetch(`http://localhost:8080/Anime/${whichId}/Favorite`, requestOptions)
            .then(response => response.json())
    }, [buttonPressed])


    function clickHandler(animeID){
        setWhichId(animeID)
        setButtonPressed(!buttonPressed)
    }




    return(
    <button className="addFavoriteButton" onCLick={clickHandler(animeID)}>*</button>
    )
}
