import React, {useEffect, useState} from "react";
import './AnimeListe.css'
import axios from "axios";


export default function AnimeListe (props){

    const {animeID, titel, img, genre, folgen, wertung} = props.anime;


const[buttonPressed, setButtonPressed] = useState[false]

    useEffect(() => {
        axios.put(`http://localhost:8080/${animeID}/Favorite`)
            .then();
    },[buttonPressed])

    const handleClick = (e) => {
    if(buttonPressed) setButtonPressed(!buttonPressed)
    else setButtonPressed(buttonPressed)
        e.preventDefault();
}
    return (
        <div>

        <ul>
                <p key={animeID} className="animeCertain">
                    <div>
                    <h1 className="animeTitel"> Titel: {titel}</h1>
                    <button className="addToFavorite" onClick={handleClick()}>*</button>
                    </div>

                    <img className="animeBild" src={img} alt='' />
                    <ul className="animeBeschreibung">
                        <h2> Genre: {genre}</h2>
                        <h2> Folgen: {folgen}</h2>
                        <h2> Wertung: {wertung}</h2>
                    </ul>
                </p>
        </ul>
    </div>

        );
}
