import logo from './logo.svg';
import './App.css';

class App extends Component {

  constructor(props) {
    super(props);
    this.state= {
      animes: [],
      isLoaded: false,
    }
  }

  componentDidMount() {

    fetch('http://localhost:8080/Anime')
        .then(res => res.json())
        .then(json => {
          this.setState({
              isLoaded: true,
              animes: json,
          })
        });
  }









}
