import axios from "axios";
import React from "react";


export class FavoriteButton extends React.Component {




    refreshList = () => {
        axios.get('http://localhost:8080/Anime')
            .then(res => this.setState({animes: res.data}));
        axios.get('http://localhost:8080/Anime/Favorite')
            .then(res => this.setState({favoriten : res.data}));
    };



    handleClickFavorit = (favorit) => {
        this.updateFavorit(favorit)
    };

    handleClickAnime = (anime) => {
        this.updateAnime(anime)
    };

    updateAnime = anime => {
        axios.put('http://localhost:8080/Anime/${anime.animeID',anime)
            .then(res => this.refreshList())
        return;
    };

    updateFavorit = favorit => {
        axios.put('http://localhost:8080/Anime/${anime.animeID',favorit)
            .then(res => this.refreshList())
        return;
    };

}
