import React, { Component} from "react";
import './App.css';
import axios from 'axios'

class App extends Component {

  constructor(props) {
    super(props);
    this.state= {
      animes: [],
    }
  }

  componentDidMount() {



    axios.get('http://localhost:8080/Anime')
    .then(res => {
      const animes = res.data;
      this.setState({animes});
    })

  }


render() {







          return(


              <div className="animeListe">

                  <ul>
                      {this.state.animes.map(anime => (
                          <p>
                              <h1> Titel: {anime.titel}</h1>
                              <img src={anime.img} alt='' height="480" width="380" />
                              <h2> Genre: {anime.genre}</h2>
                              <h3> Folgen: {anime.folgen}</h3>
                              <h4> Wertung: {anime.wertung}</h4>
                          </p>
                          ))};
                      </ul>


                  </div>


          );



}







}
export default App
