import {useEffect, useState} from "react";
import axios from "axios";
import AnimeListe from "./components/AnimeListe";
import {Anime} from "./components/Anime";

export default function App() {
    const [listenType, setListenType] = useState('Anime')
    const [animes, setAnimes] = useState([])

    useEffect(() => {
        axios.get(`http://localhost:8080/${listenType}`)
            .then(response => response.json())
            .then(json => setAnimes(json))
    }, [listenType])


return (
    <>
    <div>
        <button onClick={() => setListenType('Anime')}>Komplette Liste</button>
        <button onCLick={() => setListenType('Anime/Favorite')}>Favoriten Liste</button>
    </div>
    <h1>{listenType}</h1>
        {animes.map(anime => {
            <AnimeListe anime={anime}></AnimeListe>
        })}


    </>


)



}
