import React, {useEffect, useState} from "react";
import './AnimeListe.css'
import axios from "axios";

import updateList from "../App";

export default function AnimeListe (props){
    const {animeID, titel, img, genre, folgen, wertung,favorite} = props.anime;


    function AddFavorite ()
    {

        const newFavorite = !favorite;
        let anime = {animeID, titel, img, genre, folgen, wertung, newFavorite};



        fetch(`http://localhost:8080/Anime/${animeID}`, {
            method: 'Put',
            headers:{
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body:JSON.stringify(anime)

        }).then((result) => {
            result.json().then((resp) => {
                console.warn(resp)
            updateList()
            })
        })
        }


return (
<div>

    <ul>
        <p key={animeID} className="animeCertain">
            <div>
                <h1 className="animeTitel"> Titel: {titel}</h1>
                <button className="favoriteButton" onClick={AddFavorite}>*</button>
            </div>
            <img className="animeBild" src={img} alt='' />
            <ul className="animeBeschreibung">
                <h2> Genre: {genre}</h2>
                <h2> Folgen: {folgen}</h2>
                <h2> Wertung: {wertung}</h2>
            </ul>
        </p>
    </ul>

</div>

        );
}
