import React, {useEffect, useState} from "react";
import AnimeListe from "./components/AnimeListe";
import {Anime} from "./components/Anime";
import './App.css'
import './components/SearchBox'

export default function App() {
    const [listenType, setListenType] = useState('')
    const [animes, setAnimes] = useState([])
    const [listenName, setListenName] = useState('Komplette Liste')
    const [apiType, setApiType] = useState('http://localhost:8080/Anime')


    useEffect(() => {
    updateList()
    }, [listenType])



function updateList() {
    fetch(`${apiType}${listenType}`)
        .then(response => response.json())
        .then(json => setAnimes(json))

}



return (
    <>
    <div>
        <button className="completeButton" onClick={() => {setListenType('');setApiType('http://localhost:8080/Anime'); setListenName('Komplette Liste')}}>Komplette Liste</button>
        <button className="favoriteButton" onClick={() => {setListenType('/Favorite'); setApiType('http://localhost:8080/Anime');setListenName('Favoriten')}}>Favoriten Liste</button>
        <button className="wertungsButton" onClick={() => {setListenType('/Wertung'); setApiType('http://localhost:8080/Anime');setListenName('Top Liste')}}>Top Liste</button>
        <SearchBox></SearchBox>
    </div>
        <h1 className="homePageHeader">Der Mac Gillis Fanclub</h1>
        <h2 className="animeHeader">{listenName}: </h2>
       <p className="animeListe">
           {animes.map(anime => {
           return <AnimeListe anime={anime}></AnimeListe>
        })}
       </p>


    </>


)



}
